        if( !(flag[i/32]&(1<<(i%32))) ) {
            prime[++num] = i;
            p[num] = i * p[num-1];
            for(j = i * 3; j < N; j += i * 2)
                flag[j/32] |= 1<<(j%32);
        }
    }
}

