struct node  
{  
    int sum;  
    node *next[2];  
};  
struct node *root;  
struct node *build()  
{  
    struct node *p;  
    p=(struct node *)malloc(sizeof(struct node));  
    for(int i=0; i<2; i++)  
    {  
        p->next[i]=NULL;  
    }  
    return p;  
}  
void solve(LL s)  
{  
    memset(c,0,sizeof(c));  
    int num = 50;  
    while(s)  
    {  
        c[num--] = s%2;  
        s/=2;  
    }  
}  
void insert(LL s)  
{  
    solve(s);  
    struct node *p;  
    p=root;  
    for(int i=0; i<=50; i++)  
    {  
        if(p->next[c[i]]==NULL)  
            p->next[c[i]]=build();  
        p=p->next[c[i]];  
    }  
}  
LL find(LL s)  
{  
    solve(s);  
    struct node *p;  
    p=root;  
    LL sum = 0;  
    for(int i=0; i<=50; i++)  
    {  
        if(p->next[!c[i]]==NULL)
        {  
            sum = sum * 2;  
            p=p->next[c[i]];  
        }  
        else  
        {  
            sum = sum * 2 + 1;  
            p=p->next[!c[i]];  
        }  
    }  
    return sum;  
}  
