struct  s  
{  
    double x,y;  
}list[MAXN];  
double area(s a,s b,s c)  
{  
    return (a.x-c.x)*(b.y-c.y)-(b.x-c.x)*(a.y-c.y);  
}  
int main()  
{  
    int n;  
    int i,j,k;  
    while(scanf("%d",&n)!=EOF)  
    {  
        for(i=0;i<n;i++)  
        scanf("%lf%lf",&list[i].x,&list[i].y);  
