        for(i=0;i<n;i++)  
        {  
            for(j=0;j<n;j++)  
            {  
                if(i==j)  
                continue;  
                double M1=0,M2=0;  
                for(k=0;k<n;k++)  
                {  
                    if(k==i||k==j)  
                    continue;  
                    double a=list[i].y-list[j].y,b=list[j].x-list[i].x,c=list[i].x*list[j].y-list[j].x*list[i].y;  
