using namespace std;

const double pi = acos(-1.0);
const int inf = 0x3f3f3f3f;
const double eps = 1e-15;
typedef long long LL;
typedef pair <int, int> PLL;

const int N = 100100;
int arr[N];
int ans[N];
struct node
{
    int l, r, val;
}tree[N << 2];

void build(int p, int l, int r)
{
    tree[p].l = l;
    tree[p].r = r;
    if (l == r)
    {
        tree[p].val = arr[l];
        return;
    }
    int mid = (l + r) >> 1;
    build(p << 1, l, mid);
    build(p << 1 | 1, mid + 1, r);
    tree[p].val = min(tree[p << 1].val, tree[p << 1 | 1].val);
}

int query(int p, int l, int r, int lim)
{
    if (tree[p].l == tree[p].r)
    {
        return tree[p].val < lim ? tree[p].l : -1;
    }
    int mid = (tree[p].l + tree[p].r) >> 1;
    if (l > mid && tree[p << 1 | 1].val < lim)
    {
        int j = query(p << 1 | 1, l, r, lim);
        if (j != -1)
        {
            return j;
        }
    }
    if (r <= mid && tree[p << 1].val < lim)
    {
        int j = query(p << 1, l, r, lim);
        if (j != -1)
        {
            return j;
        }
    }
    if (l <= mid && r > mid && tree[p << 1 | 1].val < lim)
    {
        int j = query(p << 1 | 1, mid + 1, r, lim);
        if (j != -1)
        {
            return j;
        }
    }
    if (l <= mid && r > mid && tree[p << 1].val < lim)
    {
        int j = query(p << 1, l, mid, lim);
        if (j != -1)
        {
            return j;
        }
    }
    return -1;
}

int main()
{
    int n;
    while (~scanf("%d", &n))
    {
        for (int i = 1; i <= n; ++i)
        {
            scanf("%d", &arr[i]);
        }
        build(1, 1, n);
        for (int i = 1; i < n; ++i)
        {
            int j = query(1, i + 1, n, arr[i]);
            if (j == -1)
            {
                ans[i] = -1;
            }
            else
            {
                ans[i] = j - i - 1;
            }
        }
        ans[n] = -1;
        printf("%d", ans[1]);
        for (int i = 2; i <= n; ++i)
        {
            printf(" %d", ans[i]);
        }
        printf("\n");
    }
    return 0;
}

