const int MAXN=505;  
const int INF=0x7FFFFFFF;  

struct edge  
{  
    int to;  
    double weight;  
};  

vector<edge> adjmap[MAXN]; 
bool in_queue[MAXN]; 
int in_sum[MAXN];
double dist[MAXN]; 
int nodesum; 
int edgesum; 

bool SPFA(int source)  
{  
    queue<int> dq;  
    int i,j,x,to;  
    for(i=1;i<=nodesum;i++)  
    {  
        in_sum[i]=0;  
        in_queue[i]=false;  
        dist[i]=0;  
    }  
    dq.push(source);  
    in_sum[source]++;  
    dist[source]=1.0;  
    in_queue[source]=true;  

    while(!dq.empty())  
    {  
        x=dq.front();  
        dq.pop();  
        in_queue[x]=false;  
        for(i=0;i<adjmap[x].size();i++)  
        {  
            double f=adjmap[x][i].weight;  
            to=adjmap[x][i].to;  
            if(dist[to]-dist[x]*f<0.01)  
