const LL mod=1e9+7;  

LL Euler(LL n){  
    LL ans=n;  
    for(LL i=2;i<=sqrt(n);i++){  
        if(n%i==0){  
            while(n%i==0) n=n/i;  
            ans=ans/i*(i-1);  
        }  
    }  
    if(n>1) ans=ans/n*(n-1);  
    return ans;  
}  

