                if(i>=m){  
                    d=i;  
                    ans=ans+d*Euler(n/d);  
                }  
                if(i*i!=n&&n/i>=m){  
                    d=n/i;  
                    ans=ans+d*Euler(n/d);  
                }  
            }  
        }  
