#include<queue>  
#include<cmath>  
using namespace std;  
const int dx[] = {1,-1,0,0};  
const int dy[] = {0,0,1,-1};  
bool vis[105][105][5];  
char map[105][105];  
int n,m,t;  
struct node{  
    int x,y;  
    int step,st;  
};  
bool ok(int x,int y){
    if(map[x][y] == 'E'|| map[x][y] == 'D' || map[x][y] == 'X')  
        return false;  
    return true;  
}  
char graph(char c,int Bool){

    if((Bool && c == 'e') || (!Bool && c == 'd'))  
        return 'x';
    return Bool?'d':'e';  
}  
void solve(int x,int y,int Bool)
{  
    for(int i = x + 1;i < n && ok(i,y);i++)  
        map[i][y] = graph(map[i][y],Bool);  
    for(int i = x - 1;i >= 0 && ok(i,y);i--)  
        map[i][y] = graph(map[i][y],Bool);  
    for(int i = y + 1;i < m && ok(x,i);i++)  
        map[x][i] = graph(map[x][i],Bool);  
    for(int i = y - 1;i >= 0 && ok(x,i);i--)  
        map[x][i] = graph(map[x][i],Bool);  
}  
int getST(char c,int st){
    if(c == 'd') st |= 1;  
    else if(c == 'e') st |= 2;  
    else if(c == 'x') st |= 3;  
    return st;  
}  
bool find(int x,int y,int st){  
    if(!ok(x,y)) return false;  
    if(vis[x][y][st]) return false;  
    if(x < 0 || x > n || y < 0 || y > m) return false;  
    return true;  
}  
int bfs(int sta,int end){  
    memset(vis,false,sizeof(vis));  
    node p,q;  
    queue<node> Q;  
    p.x = sta,p.y = end,p.step = 0;  
    p.st = getST(map[sta][end],0);  
    vis[p.x][p.y][p.st] = true;  
    Q.push(p);  

    while(!Q.empty()){  

        q = Q.front();Q.pop();  
        if(q.st == 3) return q.step;
        for(int i = 0;i < 4;i++){  
            p.x = dx[i] + q.x;  
            p.y = dy[i] + q.y;  
            p.st = q.st;  
            if(find(p.x,p.y,p.st))  
            {  
                p.st = getST(map[p.x][p.y],p.st);  
                p.step = q.step + 1;  
                vis[p.x][p.y][p.st] = true;  
                Q.push(p);  
            }  
        }  
    }  
    return 900;  
}  
