        for(int i = 0;i < n;i++){  
            for(int j = 0;j < m;j++)  
            {  
                if(map[i][j] == 'D')  
                    solve(i,j,1);  
                else if(map[i][j] == 'E')  
                    solve(i,j,0);  
            }  
        }  
        printf("Case %d:\n",ca++);  
        int ans = bfs(sx,sy);  
        if(ans <= t)  
           printf("%d\n",ans);  
        else puts("-1");  
    }  
}

#include<stdio.h>  
