#define INF 0x3f3f3f3f  
#define ll long long  
#define N 2  
#define M 1000000007  
using namespace std;  
ll a,b,n;  
struct mat   
{  
    ll m[N][N];  
};  
mat A=  
{  
    1,1,  
    1,0  
};  
mat I=  
{  
    1,0,  
    0,1  
};  
mat multi(mat a,mat b) 
{  
    int i,j,k;  
    mat c;  
    for(i=0;i<N;i++)  
    {  
        for(j=0;j<N;j++)  
        {  
            c.m[i][j]=0;  
            for(k=0;k<N;k++)  
            {  
                c.m[i][j]=(c.m[i][j]+a.m[i][k]*b.m[k][j]%(M-1))%(M-1);  
            }  
        }  
    }  
    return c;  
}  
mat power(mat A,int k)
{  
    mat ans=I,p=A;  
    while(k)  
    {  
        if(k&1)  
            ans=multi(ans,p);     
        p=multi(p,p);  
        k>>=1;  
    }  
    return ans;  
}  
ll ppower(ll k,ll m) 
{  
    ll ans=1;  
    while(m)  
    {  
        if(m&1)  
            ans=(ans*k%M);  
        k=(k*k%M);  
        m>>=1;  
    }  
    return ans;  
}  
