    {  
        if(n==0)  
        {  
            printf("%lld\n",a);  
            continue;  
        }  
        mat ans=power(A,n-1);  
        ll ansa=ppower(a,ans.m[0][1]);  
        ll ansb=ppower(b,ans.m[0][0]);  
        ll s=(ansa*ansb)%M;  
        printf("%lld\n",s);  
    }  
    return 0;  
}

