    dist[u] = 0;  
    queue<int> q;  
    q.push (u);  
    inq[u] = true;  
    while (!q.empty())  
    {  
        u = q.front();  
        q.pop();  
        inq[u] = false;  
        for (i = pre[u]; i != -1; i = e[i].next)  
        {  
            if (e[i].h < lim) continue;  
            w = e[i].w;  
            v = e[i].v;  
            if (dist[u] + w < dist[v])  
            {  
                dist[v] = dist[u] + w;  
                if (!inq[v])  
                {  
                    q.push (v);  
                    inq[v] = true;  
                }  
            }  
        }  
    }  
}  

int main()  
{  
    int m, u, v, w, h, l, r, mid, cc = 1, res;  
    while (scanf ("%d%d", &n, &m), (n||m))  
    {  
        if (cc > 1) printf ("\n");  
        init();  
        while (m--)  
        {  
            scanf ("%d%d%d%d", &u, &v, &h, &w);  
            if (h == -1) h = inf;  
            addedge (u, v, w, h);  
            addedge (v, u, w, h);   
        }  
        scanf ("%d%d%d", &u, &v, &h);  
        l = 0, r = h, res = inf;  
        while (l < r)  
        {  
            mid = (l+r+1) >> 1;  
            spfa (u, mid);  
            if (dist[v] != inf)  
                l = mid, res = dist[v];  
            else r = mid - 1;  
        }  
        printf ("Case %d:\n", cc++);  
        if (res != inf)  
            printf ("maximum height = %d\nlength of shortest route = %d\n", l, res);  
        else puts ("cannot reach destination");  
    }  
    return 0;  
}

