};  
Point a[N];  
struct Node  
{  
    int v,len;  
};  
vector<Node> map[N];  
int n,m;  
void spfa(int s,int dis[])  
{  
    int i,pre[N];  
    bool used[N];  
    queue<int> q;  
    memset(used,0,sizeof(used));  
    memset(pre,-1,sizeof(pre));  
    for(i=0; i<N; i++)  
        dis[i]=inf;  
    dis[s]=0;  
    used[s]=true;  
    q.push(s);  
    while(!q.empty())  
    {  
        int u=q.front();  
        q.pop();  
        used[u]=false;  
        for(i=0; i<map[u].size(); i++)  
        {  
            Node p=map[u][i];  
            if(dis[p.v]>dis[u]+p.len)  
            {  
                dis[p.v]=dis[u]+p.len;  
                pre[p.v]=u;  
                if(!used[p.v])  
                {  
                    used[p.v]=true;  
                    q.push(p.v);  
                }  
            }  
        }  
    }  
    for(int i=n;pre[i]!=-1;i=pre[i])  
    {   
        for(int j=0;j<map[i].size();j++)  
        {  
            if(map[i][j].v==pre[i])  
                map[i].erase(map[i].begin()+j);  
        }  
        for(int j=0;j<map[pre[i]].size();j++)  
        {  
            if(map[pre[i]][j].v==i)  
                map[pre[i]].erase(map[pre[i]].begin()+j);  
        }  
    }  
}  
