        for(int i=0;i<m;i++)  
        {  
            int x,y,z;  
            scanf("%d%d%d",&x,&y,&z);  
            Node tmp;  
            tmp.v=y,tmp.len=z;  
            map[x].push_back(tmp);  
            tmp.v=x;  
            map[y].push_back(tmp);  
        }  
        int ans=0;  
        spfa(1,dis1);  
        ans+=dis1[n];  
        spfa(1,dis1);  
        ans+=dis1[n];  
        if(ans>=inf)  
            printf("eating\n");  
        else  
            printf("%d\n",ans);  
    }  
    return 0;  
}

