using namespace std;  
typedef long long LL;  
const LL mod = 1000000007;  
LL euler(LL x)  
{  
    LL res = x;  
    for(LL i = 2;i <= sqrt(x);i++)  
    {  
        if(x%i==0)  
        {  
            while(x%i==0) x/=i;  
            res = res/i*(i-1);  
        }  
    }  
    if(x > 1) res = res / x * ( x - 1);  
    return res;  
}  
void solve(LL m,LL r)  
{  
    LL res = 0;  
    for(LL i = 1;i <= sqrt(m);i++)  
    {  
        if(m%i==0)  
        {  
            if(m/i!=i&&m/i>=r)  
            {  
                if(i == 1|| i== 2)  
                    res = (res + m/i) % mod;  
                else  
                    res = (res + euler(i) / 2 * i * (m/i))%mod;  
            }  
            if(i >= r)  
            {  
                if(m/i == 1|| m/i == 2)  
                    res = (res + i)%mod;  
                else  
                    res = (res + euler(m/i) / 2 * (m/i) * i)%mod;  
            }  
        }  
    }  
    printf("%lld\n",res);  
}  
int main()  
{  
    LL n,k;  
    int t;  
    scanf("%d",&t);  
    while(t--)  
    {  
        scanf("%lld%lld",&n,&k);  
        solve(n,k);  
    }  
}

