void get(int p, int L, int R, int U, int V) {
    if (R < U || V < L) return;
    if (U <= L && R <= V) {
        result = min(result, tree[p]);
        return;
    }
    if (L == R) return;
    int c = p << 1, mid = (L + R) >> 1;
    get(c, L, mid, U, V);
    get(c + 1, mid + 1, R, U, V);
}

void update(int p, int L, int R, int U, int value) {
    if (U < L || R < U) return;
    if (L == R) {
        tree[p] = value;
        a[L] = value;
        return;
    }
    int c = p << 1, mid = (L + R) >> 1;
    update(c, L, mid, U, value);
    update(c + 1, mid + 1, R, U, value);
    tree[p] = min(tree[c], tree[c + 1]);
}

