int lastdigit(char* buf){  
    int len=strlen(buf),i,c,ret=1;  
    if (len==1) return mod[buf[0]-'0'];  
    for (i=0;i<len;i++) a[i]=buf[len-1-i]-'0';  
    for (;len;len-=!a[len-1]){  
        ret=ret*mod[a[1]%2*10+a[0]]%5;  
        for (c=0,i=len-1;i>=0;i--)  
            c=c*10+a[i],a[i]=c/5,c%=5;  
    }  
    return ret+ret%2*5;  
