double MAX(double q,double p)
{
	return q>p?q:p;
}
double cal(double x)
{
	int i;
	double max=-111111111;
	for(i=0;i<n;i++)	max=MAX(max,a[i]*x*x+b[i]*x+c[i]);
	return max;
}
double solve()
{
	double left,right,mid,midmid;
	double mid_cal,midmid_cal;
	left=0;
	right=1000;
	while(right-left>e)
	{
		mid=(left+right)/2;
		midmid=(mid+right)/2;
		mid_cal=cal(mid);
		midmid_cal=cal(midmid);
		if(mid_cal>midmid_cal)	left=mid;
		else					right=midmid;
	}
	return cal(right);
}
