int dfs(int s,int t)  
{  
    if (dp[s][t]!=-1) return dp[s][t];  
    if (s>t) return 0;  
    if (s==t) return dp[s][t]=a[s];  
    int x=min(dfs(s+1,t),dfs(s,t-1));  
    dp[s][t]=sum[t]-sum[s-1]-x;  
    return dp[s][t];  
}  

