int same(int a, int b, int n)
{
	int t,c1,c2;
	if (a > b)
	{
		t = a;
		a = b;
		b = t;
	}
	c1 = b - a;
	c2 = (n + a) - b;
	if (c1 > 5)c1 = 5;
	if (c2 > 5)c2 = 5;
	return 10 - c1 - c2;
}

