const int MAXN = 1e5 + 10;  
typedef long long LL;  
int cnt[MAXN], pri[MAXN], num[MAXN], pri_num, mu[MAXN], vis[MAXN], a[MAXN];  

void mobius(int n)  
{  
    pri_num = 0;  
    memset(vis, 0, sizeof(vis));  
    vis[1] = mu[1] = 1;  
    for(int i = 2; i <= n; i++) {  
        if(!vis[i]) {  
            pri[pri_num++] = i;  
            mu[i] = -1;  
        }  
        for(int j = 0; j < pri_num; j++) {  
            if(i * pri[j] > n) break;  
            vis[i*pri[j]] = 1;  
            if(i % pri[j] == 0) {  
                mu[i*pri[j]] = 0;  
                break;  
            }  
            mu[i*pri[j]] = -mu[i];  
        }  
    }  
}  

LL get(int x)  
{  
    return (LL)x * (x-1) / 2;  
}  

int main()  
{  
    mobius(100005);  
    int n;  
    while(~scanf("%d",&n)) {  
        int mmax = 0;  
        for(int i = 1; i <= n; i++) {  
            scanf("%d",&a[i]);  
            mmax = max(mmax, a[i]);  
        }  
        memset(cnt, 0, sizeof(cnt));  
        memset(num, 0, sizeof(num));  
        for(int i = 1; i <= n; i++) num[a[i]]++;  
        for(int i = 1; i <= mmax; i++)  
            for(int j = i; j <= mmax; j += i)  
                cnt[i] += num[j];  
        LL ans = 0;  
        for(int i = 1; i <= mmax; i++)  
            ans += get(cnt[i]) * mu[i];  
        printf("%lld\n", ans);  
    }  
    return 0;  
}

#include<cstdio>  
#include<cstring>  
#include<algorithm>  
using namespace std;  
const int N = 210;  
#define INF 0x3fffffff  
char str[N];  
int n, dp[N][N];  

int digit_cnt(int x)  
{  
    int a = 0;  
    while(x) {  
        a++;  
        x /= 10;  
    }  
    return a;  
}  

bool check(int l, int r, int len)  
{  
    if((r - l + 1) % len) return false;  
    for(int i = l; i < l + len; i++) {  
        for(int j = i + len; j <= r; j += len)  
            if(str[i] != str[j]) return false;  
    }  
    return true;  
}  

int get_ans()  
{  
    int i, j, k;  
    n = strlen(str+1);  
    for(i = 1; i <= n; i++) dp[i][i] = 1;  
    for(i = n - 1; i >= 1; i--) {  
        for(j = i + 1; j <= n; j++) {  
            dp[i][j] = INF;  
            for(k = i; k < j; k++)  
                dp[i][j] = min(dp[i][j], dp[i][k] + dp[k+1][j]);  
            for(int len = 1; len <= j-i+1; len++) {  
                if(check(i, j, len)) {  
                    dp[i][j] = min(dp[i][j], dp[i][i+len-1] + 2 + digit_cnt((j - i + 1) / len));  
                }  
            }  
        }  
    }  
    return dp[1][n];  
}  

int main()  
{  
    while(~scanf("%s", str+1)) {  
        printf("%d\n", get_ans());  
    }  
    return 0;  
}

#include<cstdio>  
#include<cstring>  
#include<algorithm>  
using namespace std;  
typedef long long LL;  
const int N = 10010;  
#define lson l, mid, root<<1  
#define rson mid+1, r, root<<1|1  
struct node  
{  
    int l, r;  
    LL sum, odd, add;  
}a[N<<2];  
void PushUp(int root)  
{  
    a[root].sum = a[root<<1].sum + a[root<<1|1].sum;  
    a[root].odd = a[root<<1].odd + a[root<<1|1].odd;  
}  
void PushDown(int len, int root)  
{  
    if(a[root].add)  
    {  
        a[root<<1].add += a[root].add;  
        a[root<<1|1].add += a[root].add;  
        a[root<<1].sum += LL(len - (len>>1)) * a[root].add;  
        a[root<<1|1].sum += LL(len>>1) * a[root].add;  
        if(a[root].add % 2 == 1)  
        {  
            a[root<<1].odd = len - (len>>1) - a[root<<1].odd;    
            a[root<<1|1].odd = (len>>1) - a[root<<1|1].odd;   
        }  
        a[root].add = 0;  
    }  
}  
void build_tree(int l, int r, int root)  
{  
    a[root].l = l;  
    a[root].r = r;  
    a[root].add = 0;  
    a[root].odd = 0;  
    if(l == r)  
    {  
        scanf("%lld",&a[root].sum);  
        if(a[root].sum % 2 == 1)  
            a[root].odd = 1;  
        return;  
    }  
    int mid = (l + r) >> 1;  
    build_tree(lson);  
    build_tree(rson);  
    PushUp(root);  
}  
void update(int l, int r, int root, LL k)  
{  
    if(l <= a[root].l && r >= a[root].r)  
    {  
        a[root].add += k;  
        a[root].sum += LL(a[root].r - a[root].l + 1) * k;  
        if(k % 2 == 1)  
            a[root].odd = (a[root].r - a[root].l + 1) - a[root].odd;  
        return;  
    }  
    PushDown(a[root].r - a[root].l + 1, root);  
    int mid = (a[root].l + a[root].r) >> 1;  
    if(l <= mid) update(l, r, root<<1, k);  
    if(r > mid) update(l, r, root<<1|1, k);  
    PushUp(root);  
}  
LL Query(int l, int r, int root, char ch)  
{  
    if(l <= a[root].l && r >= a[root].r)  
    {  
        if(ch == 'Q') return a[root].odd;  
        else if(ch == 'S') return a[root].sum;  
    }  
    PushDown(a[root].r - a[root].l + 1, root);  
    LL ans = 0;  
    int mid = (a[root].l + a[root].r) >> 1;  
    if(l <= mid) ans += Query(l, r, root<<1, ch);  
    if(r > mid) ans += Query(l, r, root<<1|1, ch);  
    return ans;  
}  
int main()  
{  
    int n, m, i, x, y;  
    LL z;  
    char ch[5];  
    while(~scanf("%d%d",&n,&m))  
    {  
        build_tree(1, n, 1);  
        while(m--)  
        {  
            scanf("%s",ch);  
            if(ch[0] == 'A')  
            {  
                scanf("%d%d%lld",&x,&y,&z);  
                update(x, y, 1, z);  
            }  
            else  
            {  
