}p[210];  
int cmp(node a,node b)  
{  
    if(a.x==b.x)  
    return a.y<b.y;  
    return a.x<b.x;  
}  
double kk(node a,node b)  
{  
    return (a.y*1.0-b.y)/(a.x*1.0-b.x);  
}  
int main()  
{  
    int t;  
    cin>>t;  
    while(t--)  
    {  
        int n;  
       scanf("%d",&n);  
        for(int i=0;i<n;i++)  
        {  
            scanf("%d%d",&p[i].x,&p[i].y);  
        }  
        sort(p,p+n,cmp);  
        if(p[0].x==p[1].x)  
        {  
            if(p[0].y<p[1].y)  
            printf("%d %d\n%d %d\n",p[0].x,p[0].y,p[1].x,p[1].y);  
            else  
            printf("%d %d\n%d %d\n",p[1].x,p[1].y,p[0].x,p[0].y);  
            puts("");  
            continue;  
        }  
        double max=kk(p[0],p[1]);  
        double max1;  
        int flag =0,flag1=0;  

        for(int i=1;i<n-1;i++)  
        {  
            if(p[i].x==p[i+1].x)  
            {  
                if(p[i].y<p[i+1].y)  
