                }  
                else  
                {  
                     printf("%d %d\n%d %d\n",p[i+1].x,p[i+1].y,p[i].x,p[i].y);  
                }  
                puts("");  
                flag1=1;  
                break;  
            }  
            max1=kk(p[i],p[i+1]);  
            if(max1>max)  
            {  
                max=max1;  
                flag=i;  
            }  
        }  
        if(flag1==0)  
        {  
            printf("%d %d\n%d %d\n",p[flag].x,p[flag].y,p[flag+1].x,p[flag+1].y);  
            puts("");  
        }  
    }  
}

