        l2=sqrt(pow(y2-y4,2)+pow(x2-x4,2));  
        l3=sqrt(pow(y3-y4,2)+pow(x3-x4,2));  
        l4=pow(y1-y3,2)+pow(x1-x3,2);
        l=pow(y3-y2,2)+pow(x3-x2,2);
        if(sqrt(l1)==l2&&sqrt(l1)==l3&&sqrt(l1)==sqrt(l4)&&l1+l4==l)  
            printf("Yes\n");  
        else   
            printf("No\n");  
    }  
    return 0;  
}

