    }  
}s[105][105];  
int vis[105][105],n,m;  
int dir[4][2]={{0,-1},{-1,0},{0,1},{1,0}};  
priority_queue<Info>  pq;  
void bfs()  
{  
    int i,a,b,xx,yy;  
    while(pq.empty()!=true)  
    {  
        Info tt;  
        tt=pq.top();  
        xx=tt.x1;  
        yy=tt.y1;  
        pq.pop();  
        if(tt.c=='l')  
        {  
            printf("%d\n",tt.x);  
            return;  
        }  
        for(i=0;i<4;i++)          
        {  
            a=xx+dir[i][0];  
            b=yy+dir[i][1];  
            if(a<n&&a>=0&&b>=0&&b<m&&s[a][b].c>='A'&&s[a][b].c<='z'&&!vis[a][b])  
            {  
                vis[a][b]=1;  
                s[a][b].x=s[a][b].x+s[xx][yy].x;  
                pq.push(s[a][b]);  
            }  
        }  
    }  
    if(pq.empty()==true)  
    {  
        printf("-1\n");  
        return;  
    }  
}  

