                scanf("%c",&s[i][j].c);  
                s[i][j].x=0;  
                s[i][j].x1=i;  
                s[i][j].y1=j;  
                if(s[i][j].c>='A'&&s[i][j].c<='Z')  
                    s[i][j].x=s[i][j].c-64;  
                if(s[i][j].c=='s')  
                {  
                    i1=i;  
                    j1=j;  
                }  
            }  
        }  
        pq.push(s[i1][j1]);  
        vis[i1][j1]=1;  
        bfs();  
    }  
    return 0;  
}

