using namespace std;
const int N = 50005;
bool vis[N];
struct Node{
    int day, value;
    friend bool operator < (const Node a, const Node b)
    {
        return a.value > b.value;
    }
};

bool cmp(const Node a, const Node b)
{
    if (a.day != b.day)
        return a.day < b.day;
    return a.value > b.value;
}
int n;
Node node[N];
int main()
{
    while (~scanf("%d", &n) && n)
    {
        for (int i = 0; i < n; i++)
        {
            scanf("%d %d", &node[i].day, &node[i].value);

        }
        sort(node, node + n, cmp);
        priority_queue<Node> Q;
        memset(vis, false, sizeof(vis));
        for (int i = 0; i < n; i++)
        {
            if (!vis[node[i].day])
            {
                vis[node[i].day] = true;
                Q.push(node[i]);
            }
            else
            {
                if (Q.size() < node[i].day)
                    Q.push(node[i]);
                else
                {
                    Node tmp = Q.top();
                    if (tmp.value < node[i].value)
                    {
                        Q.push(node[i]);
                        Q.pop();
                    }
                }
            }
        }
        long long ans = 0;
        while (!Q.empty())
        {
            Node tmp = Q.top();
            Q.pop();
            ans += tmp.value;
        }
        cout << ans << endl;
    }

    return 0;
}

