	while(temp){
		a[i++]=temp%10;
		temp/=10;
	}
	for(int j=0;j<i;j++){
		ans+=a[j]*bb;
		bb*=b;
	}
	return ans;
}
int maxn(int n)
{
	int max=0;
	while(n){
		if(n%10>max)
			max=n%10;
		n/=10;
	}
	return max+1; 
}
