{
	int i;
	for(i=col-1;i<N;i++)
	{
		r[row-1][i]=num++;
	}
}
int insertRow(int row,int col)
{
	int i;
	for(i=row-1;i<N;i++)
	{
		r[i][col-1]=num++;
	}
}
