		{
			if(b==1)
			{
				insertCol(row,col);
				row++;
			}
			else
			{
				insertRow(row,col);
				col++;
			}
			b=1-b;
		}
		for(i=0;i<N;i++)
		{
			for(j=0;j<N;j++)
