int gcd(int a,int b){  
    int r;  
    if(a<b)  
     {  
        r=a;  
        a=b;  
        b=r;  
     }  
     while(b!=0){  
        r=a%b;  
        a=b;  
        b=r;  
     }  
     return a;  
}

