void ternary_init()  
{
	int i, j, b;   
    for(i=0;i<bit[10];i++)   
    {   
        b=i;   
        for(j=0;j<10;j++)   
        {   
            ter[i][j] =b % 3;   
            b/=3;   
        }   
    }   
}  
void init()  
{ 
    memset(g,-1,sizeof(g));  
    memset(dp,INF,sizeof(dp));  
    for(int i=0;i<n;i++)  
        dp[bit[i]][i]=0;  
}  
int main()  
{  
    int i;  
    int u,v,w;  
    ternary_init();  
    while(~scanf("%d%d",&n,&m))  
    {  
        init();  
        for(i=0;i<m;i++)  
        {  
            scanf("%d%d%d",&u,&v,&w);  
            if(g[u-1][v-1]>w||g[u-1][v-1]==-1)  
                g[u-1][v-1]=g[v-1][u-1]=w;  
        }  
        int ans=INF;  
        for(int S=0;S<bit[n];S++)  
        { 
            int flag=true;  
            for(u=0;u<n;u++)  
            {  
                if(ter[S][u]==0)  
                    flag=false;  
