                {  
                    if(u==v||g[u][v]==-1||ter[S][v]>=2)  
                        continue;  
                    dp[S+bit[v]][v]=findMin(dp[S+bit[v]][v],dp[S][u]+g[u][v]);
                }  
            }  
            if(flag)  
            {
                for(u=0;u<n;u++)  
                    ans=findMin(ans,dp[S][u]);  
            }  
        }  
        if(ans==INF)  
            printf("-1\n");  
        else printf("%d\n",ans);  
    }  
    return 0;  
}

