using namespace std;  
#define LL long long  
const LL mod = 666666;  
LL pow_mod(LL a, LL n)
{  
    LL ans = 1, temp = a;  
    while(n > 0)  
    {  
        if(n&1)  
            ans = (ans * temp) % mod;  
        temp = (temp * temp) % mod;  
        n /= 2;  
    }  
    return ans;  
}  
LL sum(LL a, LL k)
{  
    if(k == 0)  
        return 1;  
    if(k == 1)  
        return a;  
    LL ans =  1;  
    LL as = sum(a, k/2);  
    if(k & 1)  
    {  
        LL t = pow_mod(a, k/2+1);  
        ans = (as + t + (t*as)%mod) % mod;  
    }  
    else  
    {  
        LL t = pow_mod(a, k/2);  
        ans = (as + ((t*as)%mod)) % mod;  
    }  
    return ans;  
}  
int main()  
{  
    LL a, n;  
    while(~scanf("%lld%lld", &a, &n))  
    {  
        printf("%lld\n", sum(a%mod, n));
    }  
    return 0;  
}

