int main()  
{  
    int N, tempi, cnt = 0;  
    while (~scanf("%d", &N) && ++cnt) {  
        for (int i = 1; i <= N; ++i) {  
            scanf("%d", &tempi);  
            book[i] = book[i-1] + tempi;  
        }  

        for (int i = 1; i <= N; ++i) {  
            for (int k = i-1; k >= 0; --k) {  
                if (book[i] - book[k] < maxh[k])  
