#define INT long long  

bool ToA(INT a, INT b, INT c)  
{  
    if(b == c || a == b || a == c) return true; 
    INT s = max(b, c) - min(b, c);  
    a = a + 2 * min(b, c);  
    if(s % 3 == 0){  
        if(a) return true;  
        else return false;  
    }  
    else return false;  
}  
bool solve(INT a, INT b, INT c)  
{  
    if(ToA(a, b, c)) return true;  
    if(ToA(b, a, c)) return true;  
    if(ToA(c, a, b)) return true;  
    return false;  
}  

int main()  
{  
