    while(cin >> a >> b >> c){  
        if(a == 0 && b == 0 && c == 0){  
            cout << "NO" << endl;  
            continue;  
        }  
        if(solve(a, b, c)) cout << "YES" << endl;  
        else cout << "NO" << endl;  
    }  
    return 0;  
}

