#include <stdio.h>
#include <string.h>
char map[505][505];
bool vis[505][505];
int max=0;
void dfs(int x,int y)
{
	if(map[x][y]=='0'||vis[x][y]==true)
	return ;
	max++;
	vis[x][y]=true;
	dfs(x,y+1);
	dfs(x,y-1);
	dfs(x-1,y);
	dfs(x+1,y);
	dfs(x+1,y+1);
	dfs(x+1,y-1);
	dfs(x-1,y+1);
	dfs(x-1,y-1);
}
int main()
{
	int m,n,x;
	while(~scanf("%d %d %d",&m,&n,&x))
	{
		memset(vis,false,sizeof(vis));
		memset(map,0,sizeof(map));
		for(int i=0;i<m;i++)
		{
			getchar();
			for(int j=0;j<n;j++)
			{
				char ch=getchar();
				if(ch==' ')
				j--;
				else
				map[i][j]=ch;
			}
		}
		int Max=0,count=0;
		for(int i=0;i<m;i++)
		for(int j=0;j<n;j++)
		{
			if(map[i][j]=='1'&&!vis[i][j])
			{
				max=0;
				dfs(i,j);
				if(max>Max)
				Max=max;
				count++;
			}
		}
		printf("%d %d\n",count,Max*x);
	}
	return 0;
}

